import Foundation

@objc class GearEntity: BaseManagedObject {
    
    @NSManaged var code: String?
    @NSManaged var name: String?
    @NSManaged var hauls_maximum: Int16
    @NSManaged var hauls_minimum: Int16
    @NSManaged var mesh_maximum: Int16
    @NSManaged var mesh_minimum: Int16
    @NSManaged var quantity_maximum: Int16
    @NSManaged var quantity_minimum: Int16
    @NSManaged var size_maximum: Int16
    @NSManaged var size_minimum: Int16
    @NSManaged var soak_maximum: Int16
    @NSManaged var soak_minimum: Int16
    
    static let entityName = "Gear"
    
    func setData(data: [String: AnyObject]) -> Void {
        code               = getString(data["CODE"])
        name               = getString(data["NAME"])
        hauls_maximum      = getInteger(data["HAULS_MAXIMUM"])
        hauls_minimum      = getInteger(data["HAULS_MINIMUM"])
        mesh_maximum       = getInteger(data["MESH_MAXIMUM"])
        mesh_minimum       = getInteger(data["MESH_MINIMUM"])
        quantity_maximum   = getInteger(data["QUANITY_MAXIMUM"])
        quantity_minimum   = getInteger(data["QUANTITY_MINIMUM"])
        size_maximum       = getInteger(data["SIZE_MAXIMUM"])
        size_minimum       = getInteger(data["SIZE_MINIMUM"])
        soak_maximum       = getInteger(data["SOAK_MAXIMUM"])
        soak_minimum       = getInteger(data["SOAK_MINIMUM"])                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
    }
}
